import FormInput from "./Components/FormInput";
import Header from "./Components/Header";
import ResultTable from "./Components/ResultTable";
import React, { useState } from "react";

const App = () => {
  const [results, setResults] = useState(null);
  const calculateHandler = (investmentData) => {
    const yearlyData = [];

    let currentSavings = investmentData["currentSaving"];
    const yearlyContribution = investmentData["yearlySaving"];
    const expectedReturn = investmentData["interest"] / 100;
    const duration = investmentData["investment"];

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
    setResults(yearlyContribution);
    // do something with yearlyData ...
  };

  return (
    <div>
      <Header />
      <FormInput onCalculate={calculateHandler} />
      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}

      <ResultTable />
    </div>
  );
};

export default App;
