import React from "react";

const FormInput = () => {
  const onChangeCurrentSaving = (event) => {
    console.log(event.target.value);
  };
  const onChangeyearly = (event) => {
    console.log(event.target.value);
  };
  const onChangeInterest = (event) => {
    console.log(event.target.value);
  };
  const onChangeInvestment = (event) => {
    console.log(event.target.value);
  };
  const submitHandler = (event) => {
    event.preventDefault();
    console.log("submitted");
  };
  return (
    <div>
      <form className="form" onSubmit={submitHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input
              type="number"
              id="current-savings"
              onChange={onChangeCurrentSaving}
            />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input
              type="number"
              id="yearly-contribution"
              onChange={onChangeyearly}
            />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input
              type="number"
              id="expected-return"
              onChange={onChangeInterest}
            />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={onChangeInvestment} />
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    </div>
  );
};

export default FormInput;
